import Students from '../../api/collections/Students'
import Subjects from "../../api/collections/Subjects"
export default {
  data() {
    return {
      hideList:true,
      stForm:true,
      stList:false,
      sbForm:false,
      sbList:false,
      asSubject:false,
      name: "",
      email: "",
      phone: "",
      birthDate: null,
      title: "",
      selectedStudent:{},
      subjectList:[],
      subjectListId:[],
    }
  },
  meteor: {
    $subscribe: {
      'students': [],
      'subjects':[]
    },
    students () {
      return Students.find({})
    },
    subjects(){
      return Subjects.find({});
    }
  },
  methods: {
    selectedSubjectList(){
      if(this.subjectList){
        return this.subjectList.map(x=>{return x.title}).toString()
      }
      return ""
    },
    showData(student){
      let val;
      if(student.subjects){
        val = student.subjects.map(s=>{return s.title}).toString();
        console.log(val)
      }
      return val;
    },
    allocSub(sub){
      let subject = this.subjectList.find(s=>{return s._id == sub._id});
      if(!subject){
        this.subjectList.push(sub);
      }
    },
    asignSubject(student){
      console.log(student);
      //this.hideList = false;
      this.selectedStudent = student;
      this.subjectList = student.subjects ? student.subjects : [];
    },
    updateSubject(id,student){
      Meteor.call('updateSubject',id,student,(error)=>{
        if(error){
          alert(error);
        }
        else{
          console.log("success in subjectUpdate");
        }
      })
    },
    saveAllocatedSubjects(){
      event.preventDefault()
      Meteor.call('updateStudent',this.selectedStudent._id,this.subjectList,(error)=>{
        if(error){
          alert(error);
        }else{
          this.subjectList.forEach(sub => {
            isStudent = sub.students.find(s=> {return s._id == this.selectedStudent._id});
            if(isStudent){

            }
            else{
              updateSubject(sub._id,this.selectedStudent);
            }
          });
          this.subjectList=null
          console.log("success"+ this.subjectList);
        }
      })
    },
    addSubject(event){
      event.preventDefault()
      Meteor.call('createSubject',this.title,(error)=>{
        if(error){
          alert(error);
        }else{
          this.title="",
          console.log("success")
        }
      })
    },
    addStudent(event){
      event.preventDefault()
      Meteor.call('createStudent', this.name, this.email, this.phone, this.birthDate, (error) => {
        if (error) {
          alert(error.error)
        } else {
          console.log("success");
          this.name = ''
          this.email = ''
          this.phone = '',
          this.birthDate =null
        }
      })
    },
     select(id){
      switch(id){
        case "stForm":
          this.stForm = true,
          this.stList=false,
          this.sbForm=false,
          this.sbList=false,
          this.asSubject=false
          break
        case "stList":
          this.stForm = false,
          this.stList=true,
          this.sbForm=false,
          this.sbList=false,
          this.asSubject=false
          break
        case "sbForm":
          this.stForm = false,
          this.stList=false,
          this.sbForm=true,
          this.sbList=false,
          this.asSubject=false
          break
        case "sbList":
          this.stForm = false,
          this.stList=false,
          this.sbForm=false,
          this.sbList=true,
          this.asSubject=false
          break
        case "asSubject":
          this.stForm = false,
          this.stList=false,
          this.sbForm=false,
          this.sbList=false,
          this.asSubject=true
          break
      }
    }
  },

}
